mapper.select:
select bi.*,ci.class_name as className from book_info as  bi
 LEFT JOIN class_info  ci on  ci.class_id=bi.class_id
 where bi.name like #{search}
 or bi.author like #{search} or bi.introduction like #{search} or ci.class_name like #{search}

select count(*) from book_info as  bi
LEFT JOIN class_info  ci on  ci.class_id=bi.class_id
where bi.name like #{search}
or bi.author like #{search} or bi.introduction like #{search} or ci.class_name like #{search}

select bi.*,ci.class_name as className from book_info as  bi
 LEFT JOIN class_info  ci on  ci.class_id=bi.class_id
  where book_id = #{book_id}
